sys_script_client.script:
function onLoad() {

    // This uses the dotwalking superpowers of the Scratchpad to use the requestor's city & state as the default location to be used. Make sure whomever you use for testing has values in their user record for this!

    if (g_form.getValue('locatio0') != '') {
        return;
    }

    var city = g_scratchpad.city;
    var country = g_scratchpad.country;

    if (city && country) {
        g_form.setValue('locatio0', city + ', ' + country);
    } else if (city) {
        g_form.setValue('locatio0', city);
    } else if (country) {
        g_form.setValue('locatio0', country);
    }

    if (city || country) {
        g_form.showFieldMsg('locatio0', 'Value set automatically - you may override by editing', 'info');
    }
}
